using DoyleDispatchWebApp.Data;
using DoyleDispatchWebApp.Data.Enum;
using DoyleDispatchWebApp.Models;
using DoyleDispatchWebApp.Repositories;
using FluentAssertions;
using Microsoft.EntityFrameworkCore;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using Xunit;

namespace DoyleDispatchWebApp.Tests.Repositories
{
    public class PackageRepoTests
    {
        private async Task<DataContext> GetDbContext()
        {
            var options = new DbContextOptionsBuilder<DataContext>()
                .UseInMemoryDatabase(databaseName: Guid.NewGuid().ToString())
                .Options;
            var databaseContext = new DataContext(options);
            databaseContext.Database.EnsureCreated();
            if (await databaseContext.Packages.CountAsync() < 0)
            {
                for (int i = 0; i < 10; i++)
                {
                    databaseContext.Packages.Add(
                        new Package()
                        {
                            PackageName = "Hand Fan",
                            PackageCategory = PackageCategory.Unbreakables,
                            DropDate = new DateTime(23 - 02 - 11),
                            Image = "data:image/jpeg;base64,",
                            Destination = new Destination
                            {
                                Name = "Asiwaju Daniel",
                                State = "Lagos",
                                Address = "112, haruna bus stop, ikorodu",
                                Contact = "09126175053"
                            }
                        });
                    await databaseContext.SaveChangesAsync();
                }
            }
            return databaseContext;
        }
        [Fact]
        public async void PackageRepo_Add_ReturnsBool()
        {
            //Arrange
            var package = new Package()
            {
                PackageName = "Hand Fan",
                PackageCategory = PackageCategory.Unbreakables,
                DropDate = new DateTime(23 - 02 - 11),
                Image = "data:image/jpeg;base64,",
                Destination = new Destination
                {
                    Name = "Asiwaju Daniel",
                    State = "Lagos",
                    Address = "112, haruna bus stop, ikorodu",
                    Contact = "09126175053"
                }
            };
            var dbContext = await GetDbContext();
            var packageRepo = new PackageRepo(dbContext);

            //Act
            var result = packageRepo.Add(package);

            //Assert
            result.Should().BeTrue();
        }
        public async void PackageRepo_GetByIdAsync_ReturnsClub()
        {
            //Arrange
            var id = 1;
            var dbContext = await GetDbContext();
            var packageRepo = new PackageRepo(dbContext);

            //Act
            var result = packageRepo.GetByIdAsync(id);

            //Assert
            result.Should().NotBeNull();
            result.Should().BeOfType<Task<Package>>();
        }
    }
}
