        public static async void SeedUsersAndRolesAsync(IApplicationBuilder app)
        {
            using var serviceScpoe = app.ApplicationServices.GetService<IServiceScopeFactory>().CreateScope();
            //Roles
            var roleManager = serviceScpoe.ServiceProvider.GetRequiredService<RoleManager<IdentityRole>>();

            if (!await roleManager.RoleExistsAsync(UserRoles.Admin))
                await roleManager.CreateAsync(new IdentityRole(UserRoles.Admin));
            if (!await roleManager.RoleExistsAsync(UserRoles.User))
                await roleManager.CreateAsync(new IdentityRole(UserRoles.User));

            //Users
            var userManager = serviceScpoe.ServiceProvider.GetRequiredService<UserManager<Client>>();
            string adminUserEmail = "sniiperactivity@gmail.com";

            var adminUser = await userManager.FindByEmailAsync(adminUserEmail);
            if (adminUser == null)
            {
                var newAdminUser = new Client()
                {
                    UserName = "sniperactivity",
                    Email = adminUserEmail,
                    EmailConfirmed = true,
                    Destination = new Destination()
                    {
                        Name = "Asiwaju Daniel",
                        State = "Lagos",
                        Address = "112, haruna bus stop, ikorodu",
                        Contact = "0912-617-1135"
                    }
                };
                await userManager.CreateAsync(newAdminUser, "Coding@1234?");
                await userManager.AddToRoleAsync(newAdminUser, UserRoles.Admin);
            }

            string appUserEmail = "user@etickets.com";

            var appUser = await userManager.FindByEmailAsync(appUserEmail);
            if (appUser == null)
            {
                var newAppUser = new Client()
                {
                    UserName = "app-user",
                    Email = appUserEmail,
                    EmailConfirmed = true,
                    Destination = new Destination()
                    {
                        Name = "Asiwaju Daniel",
                        State = "Lagos",
                        Address = "112, haruna bus stop, ikorodu",
                        Contact = "0912-617-1135"
                    }
                };
                await userManager.CreateAsync(newAppUser, "Coding@1234?");
                await userManager.AddToRoleAsync(newAppUser, UserRoles.User);
            }
        }
    }
